nginx_block:
location = ${PATH} { return 308 ${PATH}/; }

# Redirect everything under the slug to the real port on this host
location ^~ ${PATH}/ {
    # External redirect: /openhab/whatever -> http://$host:${UPSTREAM_PORT}/whatever
    rewrite ^${PATH}/(.*)$ http://$host:${UPSTREAM_PORT}/$1 permanent;
}
