nginx_block:
location = ${PATH} { return 308 ${PATH}/; }
location ^~ ${PATH}/ {
    rewrite ^${PATH}/(.*)$ /$1 break;

    proxy_http_version 1.1;
    proxy_set_header Upgrade $http_upgrade;
    proxy_set_header Connection $connection_upgrade;
    proxy_set_header Host $host;
    proxy_set_header X-Forwarded-Host $host;
    proxy_set_header X-Real-IP $remote_addr;
    proxy_set_header X-Forwarded-For $proxy_add_x_forwarded_for;
    proxy_set_header X-Forwarded-Proto $scheme;
    proxy_set_header X-Forwarded-Prefix ${PATH};
    proxy_set_header X-Forwarded-Uri $request_uri;

    # make upstream absolute redirects land under the slug
    absolute_redirect off;
    proxy_redirect ~^/$ ${PATH}/;
    proxy_redirect ~^/(.*)$ ${PATH}/$1;
    proxy_redirect ~^https?://[^/]+/(.*)$ $scheme://$host${PATH}/$1;

    # ensure session cookie works under the slug
    proxy_cookie_path / ${PATH}/;

    # let sub_filter read/alter bodies, and touch ALL types
    proxy_set_header Accept-Encoding "";
    sub_filter_once off;
    sub_filter_types *;

    # --- catch exact "/" so client-side redirects don't escape the slug ---
    sub_filter '="/"' '="${PATH}/"';
    sub_filter "='/'" "='${PATH}/'";
    sub_filter 'window.location="/"' 'window.location="${PATH}/"';
    sub_filter "window.location='/'" "window.location='${PATH}/'";
    sub_filter 'location.href="/"' 'location.href="${PATH}/"';
    sub_filter "location.href='/'" "location.href='${PATH}/'";
    sub_filter 'location.replace("/")' 'location.replace("${PATH}/")';
    sub_filter "location.replace('/')" "location.replace('${PATH}/')";

    # --- ultra-generic rewrites ---
    sub_filter '="/' '="${PATH}/';
    sub_filter "='/" "='${PATH}/";
    sub_filter 'url(/' 'url(${PATH}/';

    # Webpack dynamic chunks + <base href="/">
    sub_filter '__webpack_require__.p="/' '__webpack_require__.p="${PATH}/';
    sub_filter '<base href="/' '<base href="${PATH}/';

    # common OpenHAB absolute calls
    sub_filter '"/rest'  '"${PATH}/rest';
    sub_filter "'/rest"  "'${PATH}/rest";
    sub_filter '"/images/' '"${PATH}/images/';
    sub_filter "'/images/" "'${PATH}/images/";
    sub_filter '"/res/'   '"${PATH}/res/';
    sub_filter "'/res/"   "'${PATH}/res/";
    sub_filter '"/js/'    '"${PATH}/js/';
    sub_filter "'/js/"    "'${PATH}/js/";
    sub_filter '"/auth'   '"${PATH}/auth';
    sub_filter "'/auth"   "'${PATH}/auth";

    proxy_pass ${UPSTREAM};
}

# (optional) fallbacks for any stray root-absolute requests
location ^~ /images/ { proxy_set_header Accept-Encoding ""; proxy_pass ${UPSTREAM}; }
location ^~ /res/    { proxy_set_header Accept-Encoding ""; proxy_pass ${UPSTREAM}; }
location ^~ /js/     { proxy_set_header Accept-Encoding ""; proxy_pass ${UPSTREAM}; }
location ^~ /rest/   { proxy_set_header Accept-Encoding ""; proxy_pass ${UPSTREAM}; }
location ^~ /auth    { proxy_set_header Accept-Encoding ""; proxy_pass ${UPSTREAM}; }
location = /favicon.ico          { proxy_pass ${UPSTREAM}; }
location = /manifest.json        { proxy_pass ${UPSTREAM}; }
location = /manifest.webmanifest { proxy_pass ${UPSTREAM}; }
