nginx_block:
location = ${PATH} { return 308 ${PATH}/; }

# --- Main slug block (no redirect mangling) ---
location ^~ ${PATH}/ {
    rewrite ^${PATH}/(.*)$ /$1 break;

    proxy_http_version 1.1;
    proxy_set_header Upgrade $http_upgrade;
    proxy_set_header Connection $connection_upgrade;
    proxy_set_header Host $host;
    proxy_set_header X-Real-IP $remote_addr;
    proxy_set_header X-Forwarded-For $proxy_add_x_forwarded_for;
    proxy_set_header X-Forwarded-Proto $scheme;
    proxy_set_header X-Forwarded-Host $host;
    proxy_set_header X-Forwarded-Prefix ${PATH};
    proxy_set_header X-Forwarded-Uri $request_uri;

    # openHAB 3+/5 auth requirements
    add_header Set-Cookie X-OPENHAB-AUTH-HEADER=1 always;
    proxy_set_header Authorization "";

    # ensure session cookies work under the slug
    proxy_cookie_path / ${PATH}/;

    proxy_read_timeout 3600;
    proxy_pass ${UPSTREAM};
}

# (optional) tiny fallbacks you found useful
location = /favicon.ico          { proxy_pass ${UPSTREAM}; }
location = /manifest.json        { proxy_pass ${UPSTREAM}; }
location = /manifest.webmanifest { proxy_pass ${UPSTREAM}; }
