nginx_block:
location = ${PATH} { return 308 ${PATH}/; }
location ^~ ${PATH}/ {
    rewrite ^${PATH}/(.*)$ /$1 break;

    proxy_http_version 1.1;
    proxy_set_header Upgrade $http_upgrade;
    proxy_set_header Connection $connection_upgrade;
    proxy_set_header Host $host;
    proxy_set_header X-Real-IP $remote_addr;
    proxy_set_header X-Forwarded-For $proxy_add_x_forwarded_for;
    proxy_set_header X-Forwarded-Proto $scheme;

    # let sub_filter see plain text
    proxy_set_header Accept-Encoding "";

    # rewrite root-absolute URLs to live under ${PATH}
    sub_filter_once off;
    sub_filter_types text/html text/css application/javascript application/json text/plain;
    sub_filter 'href="/'  'href="${PATH}/';
    sub_filter 'src="/'   'src="${PATH}/';
    sub_filter 'action="/' 'action="${PATH}/';
    sub_filter 'url(/'     'url(${PATH}/';
    sub_filter '"/rest'   '"${PATH}/rest';
    sub_filter "'/rest"   "'${PATH}/rest";
    sub_filter '"/images/' '"${PATH}/images/';
    sub_filter "'/images/" "'${PATH}/images/";
    sub_filter '"/res/'    '"${PATH}/res/';
    sub_filter "'/res/"    "'${PATH}/res/";
    sub_filter '"/js/'     '"${PATH}/js/';
    sub_filter "'/js/"     "'${PATH}/js/";

    proxy_pass ${UPSTREAM};
}

# ---- Fallbacks for root-absolute requests the app makes ----
# (proxy directly instead of redirecting, so chunks/assets load reliably)
location ^~ /images/ { proxy_set_header Accept-Encoding ""; proxy_pass ${UPSTREAM}; }
location ^~ /res/    { proxy_set_header Accept-Encoding ""; proxy_pass ${UPSTREAM}; }
location ^~ /js/     { proxy_set_header Accept-Encoding ""; proxy_pass ${UPSTREAM}; }
location = /favicon.ico           { proxy_pass ${UPSTREAM}; }
location = /manifest.json         { proxy_pass ${UPSTREAM}; }
location = /manifest.webmanifest  { proxy_pass ${UPSTREAM}; }
# If anything still calls the REST API at the root, pass it through
location ^~ /rest/   { proxy_pass ${UPSTREAM}; }
